pragma solidity ^0.8.16;

// https://github.com/smartcontractkit/chainlink/blob/develop/contracts/src/v0.8/VRFConsumerBaseV2.sol

/** ****************************************************************************
 * @notice Interface for contracts using VRF randomness
 * *****************************************************************************
 * @dev PURPOSE
 *
 * @dev Reggie the Random Oracle (not his real job) wants to provide randomness
 * @dev to Vera the verifier in such a way that Vera can be sure he's not
 * @dev making his output up to suit himself. Reggie provides Vera a public key
 * @dev to which he knows the secret key. Each time Vera provides a seed to
 * @dev Reggie, he gives back a value which is computed completely
 * @dev deterministically from the seed and the secret key.
 *
 * @dev Reggie provides a proof by which Vera can verify that the output was
 * @dev correctly computed once Reggie tells it to her, but without that proof,
 * @dev the output is indistinguishable to her from a uniform random sample
 * @dev from the output space.
 *
 * @dev The purpose of this contract is to make it easy for unrelated contracts
 * @dev to talk to Vera the verifier about the work Reggie is doing, to provide
 * @dev simple access to a verifiable source of randomness. It ensures 2 things:
 * @dev 1. The fulfillment came from the VRFCoordinator
 * @dev 2. The consumer contract implements fulfillRandomWords.
 * *****************************************************************************
 * @dev USAGE
 *
 * @dev Calling contracts must inherit from VRFConsumerBase, and can
 * @dev initialize VRFConsumerBase's attributes in their constructor as
 * @dev shown:
 *
 * @dev   contract VRFConsumer {
 * @dev     constructor(<other arguments>, address _vrfCoordinator)
 * @dev       VRFConsumerBase(_vrfCoordinator) public {
 * @dev         <initialization with other arguments goes here>
 * @dev       }
 * @dev   }
 *
 * @dev The oracle will have given you an ID for the VRF keypair they have
 * @dev committed to (let's call it keyHash). Create account, fund it
 * @dev and your consumer contract as a consumer of it (see VRFCoordinatorInterface
 * @dev account management functions).
 * @dev Call requestRandomWords(keyHash, accId,
 * @dev callbackGasLimit, numWords),
 * @dev see (VRFCoordinatorInterface for a description of the arguments).
 *
 * @dev Once the VRFCoordinator has received and validated the oracle's response
 * @dev to your request, it will call your contract's fulfillRandomWords method.
 *
 * @dev The randomness argument to fulfillRandomWords is a set of random words
 * @dev generated from your requestId and the blockHash of the request.
 *
 * @dev If your contract could have concurrent requests open, you can use the
 * @dev requestId returned from requestRandomWords to track which response is associated
 * @dev with which randomness request.
 * @dev See "SECURITY CONSIDERATIONS" for principles to keep in mind,
 * @dev if your contract could have multiple requests in flight simultaneously.
 *
 * @dev Colliding `requestId`s are cryptographically impossible as long as seeds
 * @dev differ.
 *
 * *****************************************************************************
 * @dev SECURITY CONSIDERATIONS
 *
 * @dev A method with the ability to call your fulfillRandomWords method directly
 * @dev could spoof a VRF response with any random value, so it's critical that
 * @dev it cannot be directly called by anything other than this base contract
 * @dev (specifically, by the VRFConsumerBase.rawFulfillRandomWords method).
 *
 * @dev For your users to trust that your contract's random behavior is free
 * @dev from malicious interference, it's best if you can write it so that all
 * @dev behaviors implied by a VRF response are executed *during* your
 * @dev fulfillRandomWords method. If your contract must store the response (or
 * @dev anything derived from it) and use it later, you must ensure that any
 * @dev user-significant behavior which depends on that stored value cannot be
 * @dev manipulated by a subsequent VRF request.
 *
 * @dev Similarly, both miners and the VRF oracle itself have some influence
 * @dev over the order in which VRF responses appear on the blockchain, so if
 * @dev your contract could have multiple VRF requests in flight simultaneously,
 * @dev you must ensure that the order in which the VRF responses arrive cannot
 * @dev be used to manipulate your contract's user-significant behavior.
 *
 * @dev Since the block hash of the block which contains the requestRandomWords
 * @dev call is mixed into the input to the VRF *last*, a sufficiently powerful
 * @dev miner could, in principle, fork the blockchain to evict the block
 * @dev containing the request, forcing the request to be included in a
 * @dev different block with a different hash, and therefore a different input
 * @dev to the VRF. However, such an attack would incur a substantial economic
 * @dev cost. This cost scales with the number of blocks the VRF oracle waits
 * @dev until it calls responds to a request. It is for this reason that
 * @dev you can signal to an oracle you'd like them to wait longer before
 * @dev responding to the request (however this is not enforced in the contract
 * @dev and so remains effective only in the case of unmodified oracle software).
 */
abstract contract VRFConsumerBase {
    error OnlyCoordinatorCanFulfill(address have, address want);
    address private immutable vrfCoordinator;

    /**
     * @param _vrfCoordinator address of VRFCoordinator contract
     */
    constructor(address _vrfCoordinator) {
        vrfCoordinator = _vrfCoordinator;
    }

    /**
     * @notice fulfillRandomWords handles the VRF response. Your contract must
     * @notice implement it. See "SECURITY CONSIDERATIONS" above for important
     * @notice principles to keep in mind when implementing your fulfillRandomWords
     * @notice method.
     *
     * @dev VRFConsumerBase expects its subcontracts to have a method with this
     * @dev signature, and will call it once it has verified the proof
     * @dev associated with the randomness. (It is triggered via a call to
     * @dev rawFulfillRandomWords, below.)
     *
     * @param requestId The Id initially returned by requestRandomWords
     * @param randomWords the VRF output expanded to the requested number of words
     */
    function fulfillRandomWords(uint256 requestId, uint256[] memory randomWords) internal virtual;

    /**
     * @notice rawFulfillRandomWords is called by VRFCoordinator when it
     * @notice receives a valid VRF proof. rawFulfillRandomWords then calls
     * @notice fulfillRandomWords, after validating the origin of the call
     *
     * @param requestId The Id initially returned by requestRandomWords
     * @param randomWords the VRF output expanded to the requested number of words
     */
    function rawFulfillRandomWords(uint256 requestId, uint256[] memory randomWords) external {
        if (msg.sender != vrfCoordinator) {
            revert OnlyCoordinatorCanFulfill(msg.sender, vrfCoordinator);
        }
        fulfillRandomWords(requestId, randomWords);
    }
}
","keccak256":"0x8f25cf557237f53059663038b409a6a09210710ebc86bed6f416d418c02f6425","license":"MIT"},"src/v0.1/VRFCoordinator.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.16;

// https://github.com/smartcontractkit/chainlink/blob/develop/contracts/src/v0.8/VRFCoordinatorV2.sol

import "./interfaces/IPrepayment.sol";
import "./interfaces/ITypeAndVersion.sol";
import "./interfaces/IVRFCoordinatorBase.sol";
import "./libraries/VRF.sol";
import "./VRFConsumerBase.sol";
import "./CoordinatorBase.sol";

/// @title Orakl Network VRFCoordinator
/// @author Bisonai
/// @notice Accepts requests for random words either through [regular]
/// or [temporary] account by calling `requestRandomWords`
/// function. Consumers can choose which random words provider they
/// want to request random number from by specifying a keyHash that is
/// associated with such provider. Random words are partially
/// generated using unique seed created during request
/// transaction. The request for random words is concluded by emitting
/// `RandomWordsRequested` event which includes all necessary metadata
/// to provide requested random words. Off-chain oracle that was
/// requested to provide random words generates a proof using seed and
/// its private key, and submits it back to VRFCoordinator through
/// `fulfillRandomWords` function. Proof is validated that it was
/// generated by the requested oracle, and the requested number of
/// random words are populated from it. Eventually, the random words
/// are passed to consumer smart contract.
contract VRFCoordinator is IVRFCoordinatorBase, CoordinatorBase, ITypeAndVersion {
    uint32 public constant MAX_NUM_WORDS = 500;

    bytes32[] public sKeyHashes;

    struct Oracle {
        bool registered;
        address[] oracles;
    }

    /* keyHash */
    /* Oracle */
    mapping(bytes32 => Oracle) private sKeyHashToOracle;

    /* oracle */
    /* keyHash */
    mapping(address => bytes32) private sOracleToKeyHash;

    error InvalidKeyHash(bytes32 keyHash);
    error NumWordsTooBig(uint32 have, uint32 want);
    error NoSuchProvingKey(bytes32 keyHash);
    error InvalidAccRequest();

    event OracleRegistered(address indexed oracle, bytes32 keyHash);
    event OracleDeregistered(address indexed oracle, bytes32 keyHash);
    event RandomWordsRequested(
        bytes32 indexed keyHash,
        uint256 requestId,
        uint256 preSeed,
        uint64 indexed accId,
        uint32 callbackGasLimit,
        uint32 numWords,
        address indexed sender,
        bool isDirectPayment
    );
    event RandomWordsFulfilled(
        uint256 indexed requestId,
        uint256 outputSeed,
        uint256 payment,
        bool success
    );
    event PrepaymentSet(address prepayment);

    modifier onlyValidKeyHash(bytes32 keyHash) {
        if (!sKeyHashToOracle[keyHash].registered) {
            revert InvalidKeyHash(keyHash);
        }
        _;
    }

    constructor(address prepayment) {
        sPrepayment = IPrepayment(prepayment);
        emit PrepaymentSet(prepayment);
    }

    /**
     * @notice Registers an oracle and its proving key.
     * @param oracle address of the oracle
     * @param publicProvingKey key that oracle can use to submit VRF fulfillments
     */
    function registerOracle(
        address oracle,
        uint256[2] calldata publicProvingKey
    ) external onlyOwner {
        if (sOracleToKeyHash[oracle] != bytes32(0)) {
            revert OracleAlreadyRegistered(oracle);
        }

        bytes32 kh = hashOfKey(publicProvingKey);
        if (!sKeyHashToOracle[kh].registered) {
            sKeyHashToOracle[kh].registered = true;
            sKeyHashes.push(kh);
        }

        sKeyHashToOracle[kh].oracles.push(oracle);
        sOracleToKeyHash[oracle] = kh;

        emit OracleRegistered(oracle, kh);
    }

    /**
     * @notice Deregisters an oracle.
     * @param oracle address representing oracle that can submit VRF fulfillments
     */
    function deregisterOracle(address oracle) external onlyOwner {
        bytes32 kh = sOracleToKeyHash[oracle];
        if (kh == bytes32(0) || !sKeyHashToOracle[kh].registered) {
            revert NoSuchOracle(oracle);
        }
        delete sOracleToKeyHash[oracle];

        address[] storage oracles = sKeyHashToOracle[kh].oracles;
        uint256 oraclesLength = oracles.length;
        for (uint256 i; i < oraclesLength; ++i) {
            if (oracles[i] == oracle) {
                // oracles
                address lastOracle = oracles[oraclesLength - 1];
                oracles[i] = lastOracle;
                oracles.pop();

                // key hashes
                if (oraclesLength == 1) {
                    bytes32 lastKeyHash = sKeyHashes[oraclesLength - 1];
                    sKeyHashes[i] = lastKeyHash;
                    sKeyHashes.pop();

                    delete sKeyHashToOracle[kh];
                }

                break;
            }
        }

        emit OracleDeregistered(oracle, kh);
    }

    /**
     * @inheritdoc IVRFCoordinatorBase
     */
    function getRequestConfig() external view returns (uint32, bytes32[] memory) {
        return (sConfig.maxGasLimit, sKeyHashes);
    }

    /**
     * @inheritdoc IVRFCoordinatorBase
     */
    function requestRandomWords(
        bytes32 keyHash,
        uint64 accId,
        uint32 callbackGasLimit,
        uint32 numWords
    ) external nonReentrant onlyValidKeyHash(keyHash) returns (uint256) {
        (uint256 balance, uint64 reqCount, , , IAccount.AccountType accType) = sPrepayment
            .getAccount(accId);
        bool isValidReq = sPrepayment.isValidReq(accId);
        if (!isValidReq) {
            revert InvalidAccRequest();
        }
        uint8 numSubmission = 1;
        uint256 minBalance = estimateFeeByAcc(
            reqCount,
            numSubmission,
            callbackGasLimit,
            accId,
            accType
        );

        if (balance < minBalance) {
            revert InsufficientPayment(balance, minBalance);
        }

        bool isDirectPayment = false;
        uint256 requestId = requestRandomWords(
            keyHash,
            accId,
            callbackGasLimit,
            numWords,
            isDirectPayment
        );

        return requestId;
    }

    /**
     * @inheritdoc IVRFCoordinatorBase
     */
    function requestRandomWords(
        bytes32 keyHash,
        uint32 callbackGasLimit,
        uint32 numWords,
        address refundRecipient
    ) external payable nonReentrant onlyValidKeyHash(keyHash) returns (uint256) {
        uint64 reqCount = 0;
        uint8 numSubmission = 1;
        uint256 fee = estimateFee(reqCount, numSubmission, callbackGasLimit);
        if (msg.value < fee) {
            revert InsufficientPayment(msg.value, fee);
        }

        uint64 accId = sPrepayment.createTemporaryAccount(msg.sender);
        bool isDirectPayment = true;
        uint256 requestId = requestRandomWords(
            keyHash,
            accId,
            callbackGasLimit,
            numWords,
            isDirectPayment
        );
        sPrepayment.depositTemporary{value: fee}(accId);

        // Refund extra $KLAY
        uint256 remaining = msg.value - fee;
        if (remaining > 0) {
            (bool sent, ) = refundRecipient.call{value: remaining}("");
            if (!sent) {
                revert RefundFailure();
            }
        }

        return requestId;
    }

    /*
     * @notice Fulfill a randomness request
     * @param proof contains the proof and randomness
     * @param rc request commitment pre-image, committed to at request time
     * @dev simulated offchain to determine if sufficient balance is present to fulfill the request
     */
    function fulfillRandomWords(
        VRF.Proof memory proof,
        RequestCommitment memory rc,
        bool isDirectPayment
    ) external nonReentrant {
        uint256 startGas = gasleft();
        (uint256 requestId, uint256 randomness) = getRandomnessFromProof(proof, rc);

        uint256[] memory randomWords = new uint256[](rc.numWords);
        for (uint256 i = 0; i < rc.numWords; i++) {
            randomWords[i] = uint256(keccak256(abi.encode(randomness, i)));
        }

        delete sRequestIdToCommitment[requestId];
        delete sRequestOwner[requestId];

        bytes memory resp = abi.encodeWithSelector(
            VRFConsumerBase.rawFulfillRandomWords.selector,
            requestId,
            randomWords
        );

        // Call with explicitly the amount of callback gas requested
        // Important to not let them exhaust the gas budget and avoid oracle payment.
        // Do not allow any non-view/non-pure coordinator functions to be called
        // during the consumers callback code via reentrancyLock.
        // Note that callWithExactGas will revert if we do not have sufficient gas
        // to give the callee their requested amount.
        sConfig.reentrancyLock = true;
        bool success = callWithExactGas(rc.callbackGasLimit, rc.sender, resp);
        sConfig.reentrancyLock = false;

        uint256 payment = pay(rc, isDirectPayment, startGas);
        emit RandomWordsFulfilled(requestId, randomness, payment, success);
    }

    function pay(
        RequestCommitment memory rc,
        bool isDirectPayment,
        uint256 startGas
    ) internal returns (uint256) {
        if (isDirectPayment) {
            // [temporary] account
            (uint256 totalFee, uint256 operatorFee) = sPrepayment.chargeFeeTemporary(rc.accId);
            if (operatorFee > 0) {
                sPrepayment.chargeOperatorFeeTemporary(operatorFee, msg.sender);
            }
            return totalFee;
        } else {
            // [regular] account
            uint256 serviceFee = serviceFeeByAcc(rc.accId, rc.numWords);
            if (serviceFee > 0) {
                uint256 operatorFee = sPrepayment.chargeFee(rc.accId, serviceFee);
                uint256 gasFee = calculateGasCost(startGas);
                uint256 fee = operatorFee + gasFee;
                if (fee > 0) {
                    sPrepayment.chargeOperatorFee(rc.accId, fee, msg.sender);
                }
                return serviceFee + gasFee;
            } else return 0;
        }
    }

    /**
     * @notice The type and version of this contract
     * @return Type and version string
     */
    function typeAndVersion() external pure virtual override returns (string memory) {
        return "VRFCoordinator v0.1";
    }

    /**
     * @notice Find key hash associated with given oracle address.
     * @return keyhash
     */
    function oracleToKeyHash(address oracle) external view returns (bytes32) {
        return sOracleToKeyHash[oracle];
    }

    /**
     * @notice Find oracles associated with given key hash.
     * @return oracle addresses
     */
    function keyHashToOracles(bytes32 keyHash) external view returns (address[] memory) {
        return sKeyHashToOracle[keyHash].oracles;
    }

    /**
     * @inheritdoc ICoordinatorBase
     */
    function pendingRequestExists(
        address consumer,
        uint64 accId,
        uint64 nonce
    ) public view returns (bool) {
        uint256 keyHashesLength = sKeyHashes.length;
        for (uint256 i = 0; i < keyHashesLength; ++i) {
            (uint256 requestId, ) = computeRequestId(sKeyHashes[i], consumer, accId, nonce);
            if (isValidRequestId(requestId)) {
                return true;
            }
        }
        return false;
    }

    /**
     * @notice Returns the proving key hash key associated with this public key
     * @param publicKey the key to return the hash of
     */
    function hashOfKey(uint256[2] memory publicKey) public pure returns (bytes32) {
        return keccak256(abi.encode(publicKey));
    }

    function requestRandomWords(
        bytes32 keyHash,
        uint64 accId,
        uint32 callbackGasLimit,
        uint32 numWords,
        bool isDirectPayment
    ) private returns (uint256) {
        if (!sPrepayment.isValidAccount(accId, msg.sender)) {
            revert InvalidConsumer(accId, msg.sender);
        }

        // No lower bound on the requested gas limit. A user could request 0
        // and they would simply be billed for the proof verification and wouldn't be
        // able to do anything with the random value.
        if (callbackGasLimit > sConfig.maxGasLimit) {
            revert GasLimitTooBig(callbackGasLimit, sConfig.maxGasLimit);
        }

        if (numWords > MAX_NUM_WORDS) {
            revert NumWordsTooBig(numWords, MAX_NUM_WORDS);
        }

        uint64 nonce = sPrepayment.increaseNonce(accId, msg.sender);
        (uint256 requestId, uint256 preSeed) = computeRequestId(keyHash, msg.sender, accId, nonce);

        sRequestIdToCommitment[requestId] = computeCommitment(
            requestId,
            block.number,
            accId,
            callbackGasLimit,
            numWords,
            msg.sender
        );
        sRequestOwner[requestId] = msg.sender;

        emit RandomWordsRequested(
            keyHash,
            requestId,
            preSeed,
            accId,
            callbackGasLimit,
            numWords,
            msg.sender,
            isDirectPayment
        );

        return requestId;
    }

    function computeRequestId(
        bytes32 keyHash,
        address sender,
        uint64 accId,
        uint64 nonce
    ) private pure returns (uint256, uint256) {
        uint256 preSeed = uint256(keccak256(abi.encode(keyHash, sender, accId, nonce)));
        uint256 requestId = uint256(keccak256(abi.encode(keyHash, preSeed)));
        return (requestId, preSeed);
    }

    function computeCommitment(
        uint256 requestId,
        uint256 blockNumber,
        uint64 accId,
        uint32 callbackGasLimit,
        uint32 numWords,
        address sender
    ) private pure returns (bytes32) {
        return
            keccak256(
                abi.encode(requestId, blockNumber, accId, callbackGasLimit, numWords, sender)
            );
    }

    function getRandomnessFromProof(
        VRF.Proof memory proof,
        RequestCommitment memory rc
    ) private view returns (uint256 requestId, uint256 randomness) {
        bytes32 keyHash = hashOfKey(proof.pk);
        if (sOracleToKeyHash[msg.sender] != keyHash) {
            revert NoSuchProvingKey(keyHash);
        }
        requestId = uint256(keccak256(abi.encode(keyHash, proof.seed)));
        bytes32 commitment = sRequestIdToCommitment[requestId];
        if (commitment == 0) {
            revert NoCorrespondingRequest();
        }
        if (
            commitment !=
            computeCommitment(
                requestId,
                rc.blockNum,
                rc.accId,
                rc.callbackGasLimit,
                rc.numWords,
                rc.sender
            )
        ) {
            revert IncorrectCommitment();
        }

        bytes32 blockHash = blockhash(rc.blockNum);

        // The seed actually used by the VRF machinery, mixing in the blockhash
        bytes memory actualSeed = abi.encodePacked(
            keccak256(abi.encodePacked(proof.seed, blockHash))
        );
        randomness = VRF.randomValueFromVRFProof(proof, actualSeed); // Reverts on failure
    }
}
","keccak256":"0x7a0912e39b581ba9d418cb68bafbc981caef2b8c42269894da6d4f6b61a8d362","license":"MIT"},"src/v0.1/interfaces/IAccount.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.16;

interface IAccount {
    /// READ-ONLY FUNCTIONS /////////////////////////////////////////////////////
    enum AccountType {
        TEMPORARY,
        FIAT_SUBSCRIPTION,
        KLAY_SUBSCRIPTION,
        KLAY_DISCOUNT,
        KLAY_REGULAR
    }

    /**
     * @notice Get an account information.
     * @return balance - KLAY balance of the account in juels.
     * @return reqCount - number of requests for this account, determines fee tier.
     * @return owner - owner of the account.
     * @return consumers - list of consumer address which are able to use this account.
     * @return accType
     */
    function getAccount()
        external
        view
        returns (
            uint256 balance,
            uint64 reqCount,
            address owner,
            address[] memory consumers,
            AccountType accType
        );

    /**
     * @notice Return an account ID that is associated with this account.
     * @return account ID
     */
    function getAccountId() external returns (uint64);

    /**
     * @notice Return an amount of KLAY held in the Account contract.
     * @return balance of account
     */
    function getBalance() external returns (uint256);

    /**
     * @notice Return the number of requests created through the
     * @notice account.
     * @return number of requests
     */
    function getReqCount() external returns (uint64);

    /**
     * @notice Return the current owner of account.
     * @return owner address
     */
    function getOwner() external returns (address);

    /**
     * @notice Return the consumers assigned to the account.
     * @return list of consumer addresses
     */
    function getConsumers() external returns (address[] memory);

    /**
     * @notice Return the requested owner of account.
     * @return requested owner address
     */
    function getRequestedOwner() external returns (address);

    /**
     * @notice Return the current nonce of given consumer.
     * @return consumer nonce
     */
    function getNonce(address consumer) external view returns (uint64);

    /**
     * @notice Return the address of payment solution associated with account.
     * @return address of payment solution
     */
    function getPaymentSolution() external view returns (address);

    /// STATE-ALTERING FUNCTIONS ////////////////////////////////////////////////

    /**
     * @notice Increase nonce for given consumer.
     * @param consumer - Address of consumer
     */
    function increaseNonce(address consumer) external returns (uint64);

    /**
     * @notice Request account owner transfer.
     * @param newOwner - proposed new owner of the account
     */
    function requestAccountOwnerTransfer(address newOwner) external;

    /**
     * @notice Request account owner transfer.
     * @dev will revert if original owner of accId has
     * not requested that msg.sender become the new owner.
     * @param newOwner - proposed new owner of the account
     */
    function acceptAccountOwnerTransfer(address newOwner) external;

    /**
     * @notice Add a consumer to an account.
     * @param consumer - New consumer which can use the account
     */
    function addConsumer(address consumer) external;

    /**
     * @notice Remove a consumer from a account.
     * @param consumer - Consumer to remove from the account
     */
    function removeConsumer(address consumer) external;

    /**
     * @notice Withdraw KLAY from account.
     * @dev Only account owner can withdraw KLAY.
     * @param amount - KLAY amount to be withdrawn
     */
    function withdraw(uint256 amount) external returns (bool, uint256);

    /**
     * @notice Burn part of fee and charge protocol fee for a service
     * connected to account.
     * @param burnFee - $KLAY amount to be burnt
     * @param protocolFee - $KLAY amount to be sent to protocol fee recipient
     * @param protocolFeeRecipient - address of Orakl Network
     */
    function chargeFee(uint256 burnFee, uint256 protocolFee, address protocolFeeRecipient) external;

    /**
     * @notice Charge operator fee for a service connected to account.
     * @param operatorFee - $KLAY amount to be send to oracle operator
     * fee recipient
     * @param operatorFeeRecipient - address of Orakl Network
     */
    function chargeOperatorFee(uint256 operatorFee, address operatorFeeRecipient) external;

    /**
     * @notice Destroy the smart contract and send the remaining $KLAY
     * @notice to `to` address.
     * @param to - Where to send the remaining KLAY to
     */
    function cancelAccount(address to) external;

    function getAccountDetail() external view returns (uint256, uint256, uint256, uint256);

    function getSubscriptionPaid() external view returns (bool);

    function updateAccountDetail(
        uint256 startDate,
        uint256 period,
        uint256 reqPeriodCount,
        uint256 subscriptionPrice
    ) external;

    function setSubscriptionPaid() external;

    function isValidReq() external view returns (bool);

    function getFeeRatio() external view returns (uint256);

    function setFeeRatio(uint256 disCount) external;

    function increaseSubReqCount() external;
}
